import { useRef, useState } from 'react';
import { Box, FormControl, Input, InputLabel } from '@mui/material';
import emailjs from '@emailjs/browser';
import { PortfolioButton, FormTextField } from '../../CustomStyles';


const ContactForm = () => {
    const form = useRef();
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');

    const sendEmail = (e) => {
        e.preventDefault();
        emailjs.sendForm('service_8hgpaeg', 'template_7g06df7', {user_name: name, user_email: email, message: message}, 'wBwLhMg24QBp3IbkY')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
    }

    return(
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: '20px', alignItems: 'center' }}>
            <FormTextField
                id="outlined-basic"
                label="Jméno a Příjmení"
                type="text"
                name="user_name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                fullWidth
                required
            />
            <FormTextField
                id="outlined-basic"
                label="Email"
                type="email"
                name="user_email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                fullWidth
                required
            />
            <FormTextField
                id="outlined-multiline-static"
                label="Vaše zpráva"
                name="message"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                fullWidth
                multiline
                rows={8}
                required
            />
            <PortfolioButton variant='contained' onClick={sendEmail} >
                    Odeslat zprávu
            </PortfolioButton>
            {/* <FormControl onSubmit={sendEmail} ref={form}>
                <InputLabel>Name</InputLabel>
                <Input type="text" name="user_name" value={name} onInput={(e) => setName(e.target.value)}/>
                <br />
                <InputLabel>Email</InputLabel>
                <Input type="email" name="user_name" value={email} onInput={(e) => setEmail(e.target.value)}/>
                <InputLabel>Text</InputLabel>
                <Input type="text" name="user_name" value={message} onInput={(e) => setMessage(e.target.value)}/>
                <PortfolioButton type="submit" value="Send">Odeslat</PortfolioButton>
            </FormControl> */}
            <form ref={form} onSubmit={sendEmail}>
      <label>Name</label>
      <input type="text" name="user_name" />
      <label>Email</label>
      <input type="email" name="user_email" />
      <label>Message</label>
      <textarea name="message" />
      <input type="submit" value="Send" />
    </form>
        </Box>
    );
};

export default ContactForm;
