import { useRef, useState } from 'react';
import { Box, FormControl, Input, InputLabel } from '@mui/material';
import emailjs from '@emailjs/browser';
import { PortfolioButton, FormTextField } from '../../CustomStyles';


const ContactForm = () => {
    const form = useRef();
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');

    const sendEmail = (e) => {
        e.preventDefault();
        emailjs.sendForm('service_8hgpaeg', 'template_7g06df7', form.current, 'wBwLhMg24QBp3IbkY')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
    }

    return(
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: '20px', alignItems: 'center' }}>
            {/* <FormTextField id="outlined-basic" label="Jméno a Příjmení" fullWidth required />
            <FormTextField id="outlined-basic" label="Email" fullWidth required />
            <FormTextField id="outlined-multiline-static" label="Vaše zpráva" fullWidth multiline rows={8} required />
            <PortfolioButton variant='contained' type="submit">
                    Odeslat zprávu
            </PortfolioButton>    */}
            <FormControl onSubmit={sendEmail}>
                <InputLabel>Name</InputLabel>
                <Input type="text" name="user_name" value={name} onInput={(e) => setName(e.target.value)}/>
                <InputLabel>Email</InputLabel>
                <Input type="email" name="user_name" value={name} onInput={(e) => setName(e.target.value)}/>
                <InputLabel>Text</InputLabel>
                <Input type="text" name="user_name" value={name} onInput={(e) => setName(e.target.value)}/>
                <PortfolioButton type="submit">Odeslat</PortfolioButton>
            </FormControl>
        </Box>
    );
};

export default ContactForm;
